sqlMap.select:
SELECT A.NOTICENO, A.MEMBERNO, A.NOTICETITLE, A.NOTICECONTENT, A.NOTICEWRITERDATE, A.NOTICELOOKUP, A.NICK FROM (SELECT ROW_NUMBER() OVER(ORDER BY NB.NOTICENO DESC) AS RNUM, NB.NOTICENO, NB.MEMBERNO, NB.NOTICETITLE, NB.NOTICECONTENT, to_char(NB.NOTICEWRITERDATE, 'yyyy-mm-dd') AS NOTICEWRITERDATE, NB.NOTICELOOKUP, MI.NICK FROM NOTICEBOARD NB LEFT OUTER JOIN MEMBERINFO MI ON NB.MEMBERNO = MI.MEMBERNO WHERE 1 = 1 AND NOTICEDEL = 1
AND NOTICETITLE LIKE '%' || #searchText# || '%'
) A WHERE A.RNUM BETWEEN #start# AND #end#
